import React from"react";import{Navigate}from"react-router-dom";import axios from"axios";export const refreshPage=()=>{window.location.reload()};export const getUser=()=>{const userStr=localStorage.getItem("user");return userStr?JSON.parse(userStr):null};export const getRoleId=()=>localStorage.getItem("role_id")||null;export const getToken=()=>localStorage.getItem("token")||null;export const logOut=()=>{axios({method:"post",url:window.$link+"logout/index/"+window.$userId,withCredentials:!1,params:{api_key:window.$api_key}}).then((function(message){}))};export const removeUserSession=()=>{localStorage.removeItem("token"),localStorage.removeItem("user"),logOut(),refreshPage()};export const setUserSession=(token,user)=>{localStorage.setItem("token",token),localStorage.setItem("user",JSON.stringify(user))};