import React from"react";import{Navigate}from"react-router-dom";export const refreshPage=()=>{window.location.reload()};export const getUser=()=>{const userStr=localStorage.getItem("name");return userStr?JSON.parse(userStr):null};export const getToken=()=>localStorage.getItem("token")||null;export const removeUserSession=()=>{localStorage.removeItem("token"),localStorage.removeItem("user"),refreshPage()};export const setUserSession=(token,user)=>{localStorage.setItem("token",token),localStorage.setItem("user",JSON.stringify(user))};